import { useState } from "react";
import useInput from "../hooks/use-input";

const SimpleInput = (props) => {
  const {
    hasError: nameHasError,
    enteredValue: enteredName,
    isValid: nameIsValid,
    changeHandler: inputNameChangeHandler,
    onBlurHandler: onBlurNameHandler,
    reset: resetNameInput,
  } = useInput((value) => value.trim() !== "");

  const nameInputClassname = nameHasError
    ? "form-control invalid"
    : "form-control";

  // E-mail
  const [enteredEmail, setEnteredEmail] = useState("");
  const [inputEmailTouched, setInputEmailTouched] = useState(false);
  const enteredEmailIsValid = enteredEmail.trim() !== "";
  const inputEmailIsInvalid = inputEmailTouched && !enteredEmailIsValid;
  const emailInputClassname = inputEmailIsInvalid
    ? "form-control invalid"
    : "form-control";

  // Form validity
  let formIsValid = false;

  if (nameIsValid && enteredEmailIsValid) {
    formIsValid = true;
  }

  // const inputNameChangeHandler = (event) => {
  //   setEnteredName(event.target.value);
  // };

  // const onBlurNameHandler = () => {
  //   setInputNameTouched(true);
  // };

  const inputEmailChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  };

  const onBlurEmailHandler = () => {
    setInputEmailTouched(true);
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    // setInputNameTouched(true);
    setInputEmailTouched(true);
    if (!nameIsValid || !enteredEmailIsValid) {
      return;
    }
    // setEnteredName("");
    // setInputNameTouched(false);
    resetNameInput();
    setEnteredEmail("");
    setInputEmailTouched(false);
  };

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClassname}>
        <label htmlFor="name">Your Name</label>
        <input
          onChange={inputNameChangeHandler}
          onBlur={onBlurNameHandler}
          type="text"
          id="name"
          value={enteredName}
        />
        {nameHasError && <p className="error-text">Name must not be empty.</p>}
      </div>
      <div className={emailInputClassname}>
        <label htmlFor="email">Your E-mail</label>
        <input
          onChange={inputEmailChangeHandler}
          onBlur={onBlurEmailHandler}
          type="email"
          id="email"
          value={enteredEmail}
        />
        {inputEmailIsInvalid && (
          <p className="error-text">Please enter a valid email.</p>
        )}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
