import { useState, useMemo } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [inputNameTouched, setInputNameTouched] = useState(false);

  const enteredNameIsValid = enteredName.trim() !== "";
  const nameInputClassname = inputNameIsInvalid
    ? "form-control invalid"
    : "form-control";

  const inputNameChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const onBlurHandler = () => {
    setInputNameTouched(true);
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    setInputNameTouched(true);
    if (!enteredNameIsValid) {
      return;
    }
    setEnteredName("");
    setInputNameTouched(false);
  };

  const inputNameIsInvalid = useMemo(
    () => inputNameTouched && !enteredNameIsValid,
    [inputNameTouched, enteredNameIsValid]
  );

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClassname}>
        <label htmlFor="name">Your Name</label>
        <input
          onChange={inputNameChangeHandler}
          onBlur={onBlurHandler}
          type="text"
          id="name"
        />
        {inputNameIsInvalid && (
          <p className="error-text">Name must not be empty.</p>
        )}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
